mapper.update:
update ORG_ORGANIZATION_INFO 
	set status = '0' 
where code = #{code}

mapper.select:
SELECT 

FROM ORG_AUDIT_LOG a

WHERE a.id = #{id}

SELECT 

FROM ORG_AUDIT_LOG a


	a.item_code='0' 

		AND a.org_code LIKE '%'||#{orgCode}||'%'


		AND b.name LIKE '%'||#{orgName}||'%'


		AND a.status = #{status}


		AND TO_CHAR(a.credt, 'YYYY-MM-DD') >= #{submitSdate} 


		AND TO_CHAR(a.credt, 'YYYY-MM-DD') <= #{submitEdate} 




		ORDER BY ${page.orderBy}


		ORDER BY a.credt DESC

SELECT 

FROM ORG_AUDIT_LOG a

WHERE a.org_code = #{orgCode} and a.item_code = '0'	and a.credt = #{credt}

mapper.update:
update org_audit_log set 
	status=#{status},
	moddt=#{updateDate},
	operator=#{operator.id},
	msg=#{msg}
where id=#{id} and moddt=#{moddt}
